import React, { useEffect, useState } from "react";

export default function ToDo() {
  const [textInput, setTextInput] = useState("");
  const [listTasks, setListTasks] = useState([]);

  // Charger les tâches depuis le localStorage au montage
  useEffect(() => {
    const savedTasks = localStorage.getItem("tasks");
    if (savedTasks) {
      setListTasks(JSON.parse(savedTasks));
    }
  }, []);

  // Sauvegarder les tâches dans le localStorage à chaque modification
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(listTasks));
  }, [listTasks]);

  // Ajouter une tâche
  function handleAddClick(e) {
    e.preventDefault();
    if (textInput.trim() === "") return; // Ne pas ajouter une tâche vide

    setListTasks((prev) => [...prev, textInput]); // Ajouter la tâche de manière immuable
    setTextInput(""); // Réinitialiser l'input
  }

  // Supprimer une tâche
  function handleDelete(index) {
    setListTasks((prev) => prev.filter((_, i) => i !== index));
  }

  return (
    <div className="justify-center flex items-center h-screen">
      <form className="bg-teal-700 p-3">
        <h1 className="text-center font-bold">Tâches du jour</h1>
        <div>
          <input
            className="p-1 m-1"
            value={textInput}
            onChange={(event) => setTextInput(event.target.value)}
            placeholder="Ajouter une tâche"
          />
          <button
            onClick={handleAddClick}
            className="bg-blue-600 hover:bg-blue-900 text-white p-1 m-1"
          >
            Ajouter
          </button>
        </div>
        <hr />
        <div>
          {listTasks.map((task, index) => (
            <div key={index} className="flex items-center my-2">
              <p className="flex-grow">{task}</p>
              <button
                onClick={() => alert("Tâche validée !")}
                className="bg-green-500 hover:bg-green-700 text-white px-2 mx-1"
              >
                Valider
              </button>
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 hover:bg-red-700 text-white px-2 mx-1"
              >
                Supprimer
              </button>
              <button
                onClick={() => alert("Fonction d'édition à venir...")}
                className="bg-blue-500 hover:bg-blue-700 text-white px-2 mx-1"
              >
                Éditer
              </button>
            </div>
          ))}
        </div>
      </form>
    </div>
  );
}
