import React, { useEffect, useState } from "react";
export default function ToDo(){

    const [textInput,setTextInput]=useState("")
    const [listTasks ,setListTasks]=useState([])

    useEffect(()=>{
        const savedTasks=localStorage.getItem("tasks")
        if (savedTasks){
            setListTasks(JSON.parse(savedTasks))
        }
    },[])
    useEffect(()=>{
        localStorage.setItem("tasks",JSON.stringify(listTasks))
    })
    function handelAddClick(e) {
    e.preventDefault();
    if (textInput.trim() === "") return; // Ne pas ajouter de tâche vide

    setListTasks((prev) => [...prev, textInput]); // Mettre à jour la liste de manière immuable
    setTextInput(""); // Réinitialiser l'input
}

    return (
        <>
        <div className="justify-center flex items-center h-screen">
            <form className="bg-teal-700 p-3">
                <h1 className="text-center font-bold">Taches Du jour</h1>
                <div>
                <input className="p-1 m-1" value={textInput} onChange={(event)=>
                    setTextInput(event.target.value)
                }/>
                <button onClick={handelAddClick} className="bg-blue-600 hover:bg-blue-900 text-white p-1 m-1">Add</button>
                </div> 
                <hr className=""></hr>
{}

